import React, { useState } from 'react';

function AdventureGame({ onComplete }) {
  const [storyStep, setStoryStep] = useState(0);
  const story = [
    "You find yourself in a magical garden filled with memories.",
    "A path diverges in front of you—one leads to a fountain of love, the other to a meadow of laughter.",
    // More narrative steps can be added here.
  ];

  const nextStory = () => {
    if (storyStep + 1 < story.length) {
      setStoryStep(storyStep + 1);
    } else {
      // When the narrative ends, display the final question.
      onComplete();
    }
  };

  return (
    <div>
      <h2>Choose Your Own Adventure</h2>
      <p>{story[storyStep]}</p>
      <button onClick={nextStory}>Next</button>
      {storyStep === story.length - 1 && (
        <div style={{ marginTop: '20px' }}>
          <p>No matter what you chose, one thing remains the same…</p>
          <p><strong>Will you be my Valentine? ❤️</strong></p>
          <button onClick={() => alert("She said YES! ❤️")} style={{ marginRight: '10px' }}>Yes</button>
          <button onClick={() => alert("She said YES! ❤️")}>Of course, YES!</button>
        </div>
      )}
    </div>
  );
}

export default AdventureGame;
