import React, { useState } from 'react';
import Confetti from 'react-confetti';
import './FinalMessage.css';

const FinalMessage = () => {
  // State to show/hide the confetti
  const [showConfetti, setShowConfetti] = useState(false);

  // State to track if "No" is hovered => it will shrink "No" and grow "Yes"
  const [noHover, setNoHover] = useState(false);

  // Handle clicking "Yes"
  const handleYesClick = () => {
    setShowConfetti(true); // This will show confetti
  };

  // "No" is not clickable; we can prevent default or do nothing
  const handleNoClick = (e) => {
    e.preventDefault();
    // Optionally do something funny here
  };

  // When mouse enters "No" button area
  const handleNoMouseEnter = () => {
    setNoHover(true); // triggers CSS to shrink "No" and grow "Yes"
  };

  // When mouse leaves "No" button area
  const handleNoMouseLeave = () => {
    setNoHover(false);
  };

  return (
    <div className="final-section">
      {/* Conditionally render the Confetti component */}
      {showConfetti && <Confetti />}

      <h2>You Did It!</h2>
      <p className="valentine-text">Will you be my Valentine?</p>

      <div className="button-row">
        <button
          className={`yes-button ${noHover ? 'grow' : ''}`}
          onClick={handleYesClick}
        >
          Yes
        </button>

        <button
          className={`no-button ${noHover ? 'shrink' : ''}`}
          onClick={handleNoClick}
          onMouseEnter={handleNoMouseEnter}
          onMouseLeave={handleNoMouseLeave}
        >
          No
        </button>
      </div>
    </div>
  );
};

export default FinalMessage;
