import React, { useState } from 'react';
import Confetti from 'react-confetti';
import './FinalMessage.css';

const FinalMessage = () => {
  const [showConfetti, setShowConfetti] = useState(false);
  // Whether the No button was clicked (makes the No shrink, Yes grow)
  const [noClicked, setNoClicked] = useState(false);

  // Clicking "Yes" triggers confetti
  const handleYesClick = () => {
    setShowConfetti(true);
  };

  // Clicking "No" just sets the noClicked state
  const handleNoClick = (e) => {
    e.preventDefault(); // prevent any default action
    setNoClicked(true); 
  };

  return (
    <div className="final-section">
      {showConfetti && <Confetti />}

      <h2>You Did It!</h2>
      <p className="valentine-text">Will you be my Valentine?</p>

      <div className="button-row">
        {/* Yes button grows if noClicked is true */}
        <button
          className={`yes-button ${noClicked ? 'grow' : ''}`}
          onClick={handleYesClick}
        >
          Yes
        </button>

        {/* No button shrinks if noClicked is true */}
        <button
          className={`no-button ${noClicked ? 'shrink' : ''}`}
          onClick={handleNoClick}
        >
          No
        </button>
      </div>
    </div>
  );
};

export default FinalMessage;
