import React, { useState } from 'react';
import Confetti from 'react-confetti';
import './FinalMessage.css';

const FinalMessage = () => {
  const [showConfetti, setShowConfetti] = useState(false);
  const [noClicks, setNoClicks] = useState(0); 
  // noClicks tracks how many times "No" was clicked

  // "Yes" triggers confetti
  const handleYesClick = () => {
    setShowConfetti(true);
  };

  // Each "No" click increments the counter
  const handleNoClick = (e) => {
    e.preventDefault();
    setNoClicks((prevCount) => prevCount + 1);
  };

  // We compute scale factors based on noClicks
  // For smaller increments, let's do 0.05 (5%) per click
  // Starting from a bigger base scale for each button
  const baseYesScale = 1.2; 
  const baseNoScale = 1.2;

  // Each "No" click grows the Yes button +0.05
  const yesScale = baseYesScale + noClicks * 0.05;
  // Each "No" click shrinks the No button -0.05
  const noScale = baseNoScale - noClicks * 0.05;

  return (
    <div className="final-section">
      {/* Conditionally render confetti */}
      {showConfetti && <Confetti />}

      <h2>You Did It!</h2>
      <p className="valentine-text">Will you be my Valentine?</p>

      <div className="button-row">
        <button
          className="yes-button"
          style={{
            transform: `scale(${yesScale})`,
          }}
          onClick={handleYesClick}
        >
          Yes
        </button>

        <button
          className="no-button"
          style={{
            transform: `scale(${noScale})`,
          }}
          onClick={handleNoClick}
        >
          No
        </button>
      </div>
    </div>
  );
};

export default FinalMessage;
