import React, { useState, useEffect } from 'react';
import './SideSlideshow.css';

/**
 * A side slideshow that automatically cycles through images.
 * Position this component absolutely or fixed on the side of your main content.
 */
const SideSlideshow = () => {
  // 1) Define your images array
  const images = [
    {
      src: '../../public/images/image1.jpeg',
      alt: 'Us at the park',
    },
    {
      src: '/images/image2.jpeg',
      alt: 'Us on vacation',
    }
    // ... add more as needed
  ];

  // 2) State: which image index is currently visible
  const [currentIndex, setCurrentIndex] = useState(0);

  // 3) useEffect to set up auto-slide
  useEffect(() => {
    // Slide every 3 seconds (3000 ms). Adjust as desired.
    const slideInterval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 3000);

    // Cleanup on unmount
    return () => clearInterval(slideInterval);
  }, [images.length]);

  // 4) Return the slideshow container
  return (
    <div className="side-slideshow-container">
      {/* We'll just render the current image */}
      {images.map((img, index) => (
        <div
          className="slideshow-image-wrapper"
          key={index}
          style={{
            // If it's the currentIndex, show; else hide
            opacity: index === currentIndex ? 1 : 0,
            zIndex: index === currentIndex ? 2 : 1,
          }}
        >
          <img src={img.src} alt={img.alt} className="slideshow-image" />
        </div>
      ))}
    </div>
  );
};

export default SideSlideshow;
