// src/components/PuzzleGame.js
import React, { useState } from 'react';
import Draggable from 'react-draggable';
import { motion } from 'framer-motion';
import './PuzzleGame.css';

const initialPieces = [
  {
    id: 1,
    image: '/images/piece1.png', // Make sure the image exists in public/images/
    x: Math.random() * 300,
    y: Math.random() * 200,
    target: { x: 50, y: 50 },
    width: 100,
    height: 100,
    locked: false,
  },
  {
    id: 2,
    image: '/images/piece2.png',
    x: Math.random() * 300,
    y: Math.random() * 200,
    target: { x: 160, y: 50 },
    width: 100,
    height: 100,
    locked: false,
  },
  // Add more pieces as needed.
];

function PuzzleGame({ onComplete }) {
  const [pieces, setPieces] = useState(initialPieces);

  const handleDragStop = (id, e, data) => {
    setPieces((prevPieces) =>
      prevPieces.map((piece) => {
        if (piece.id === id) {
          const deltaX = Math.abs(data.x - piece.target.x);
          const deltaY = Math.abs(data.y - piece.target.y);
          if (deltaX < 20 && deltaY < 20) {
            return { ...piece, x: piece.target.x, y: piece.target.y, locked: true };
          }
          return { ...piece, x: data.x, y: data.y };
        }
        return piece;
      })
    );
  };

  const isComplete = pieces.every((piece) => piece.locked);
  if (isComplete) {
    setTimeout(onComplete, 500); // slight delay for a better effect
  }

  return (
    <div className="puzzle-container">
      {pieces.map((piece) => (
        <Draggable
          key={piece.id}
          position={{ x: piece.x, y: piece.y }}
          disabled={piece.locked}
          onStop={(e, data) => handleDragStop(piece.id, e, data)}
        >
          <motion.div
            className={`puzzle-piece ${piece.locked ? 'locked' : ''}`}
            style={{
              width: piece.width,
              height: piece.height,
              backgroundImage: `url(${piece.image})`,
            }}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          />
        </Draggable>
      ))}
      {isComplete && (
        <motion.div className="complete-overlay" initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
          <h2>Great job!</h2>
          <p>
            You unlocked the hidden message: "Great job! Now, letâ€™s walk through our love story!"
          </p>
          <button onClick={onComplete}>Next Stage</button>
        </motion.div>
      )}
    </div>
  );
}

export default PuzzleGame;
