import React from 'react';
import './FloatingHearts.css';

const FloatingHearts = () => {
  // For variety, we can create an array of hearts with different delays/positions
  const heartsArray = Array.from({ length: 8 }, (_, i) => i);

  return (
    <div className="floating-hearts-container">
      {heartsArray.map((index) => {
        // Random left position and animation delay:
        const leftPosition = Math.random() * 90; // 0–90% 
        const delay = Math.random() * 5;        // seconds
        return (
          <div
            className="heart"
            key={index}
            style={{ left: `${leftPosition}%`, animationDelay: `${delay}s` }}
          />
        );
      })}
    </div>
  );
};

export default FloatingHearts;
